const inpStep=document.querySelector("#inputStep"),txtUser=document.querySelector("#textUser"),txtEncrypt=document.querySelector("#textEncrypt"),msg=document.querySelector(".msg"),rdEncrypt=document.querySelector("#radioEncrypt"),rdDecrypt=document.querySelector("#radioDecrypt"),btnClean=document.querySelector("#btnClean");function clearForms(){inpStep.value="",txtUser.value="",txtEncrypt.value="",msg.innerHTML=""}function crypt(t){var r=inpStep.value%33,n=t.toLowerCase();let c="абвгдеёжзийклмнопрстуфхцчшщъыьэюя".split(""),o="",u=0;for(let e=0;e<n.length;e++){var l,p=n[e];" "!==p?(l=c.indexOf(p),!0===rdEncrypt.checked&&(u=l+r),!0===rdDecrypt.checked&&(u=l-r),32<u&&(u-=33),u<0&&(u+=33),t[e]===t[e].toUpperCase()?o+=c[u].toUpperCase():o+=c[u]):o+=p}return o}function countLetter(){return txtEncrypt.value.split(" ").join("").length}txtUser.addEventListener("keyup",()=>{txtEncrypt.value=crypt(txtUser.value),msg.innerHTML="Всего символов без пробелов: "+countLetter()}),btnClean.onclick=function(){clearForms()},rdEncrypt.onclick=function(){clearForms()},rdDecrypt.onclick=function(){clearForms()};